oría% MAT(I,2) = 121;               identificador del modelo material% MAT(I,3) = FCCO;              resistencia a la compresión% MAT(I,4) = ECCO;              deformación máxima% MAT(I,5) = ECSR;              deformación de agrietamiento% MAT(I,6) = ECSP;              deformación de desprendimiento% MAT(I,7) = FCTR;              resistencia a la tracción% MAT(I,8) = POII;              relación de Poisson inicial% MAT(I,9) = POIM;              relación de Poisson máxima% MAT(I,10) = RECB;              recubrimiento% MAT(I,11) = RTRE;             espaciamiento entre ref transv% MAT(I,12) = RTRT;             tipo de refuerzo transversal% MAT(I,13) = RTRD;             díametro del ref transversal% MAT(I,14) = NBLO;             número de barras de ref long% MAT(I,15) = EFRA;             desns de energía de frac acero% MAT(I,16) = PFRA;             potencia P en la ec del acero% ------------------------------------------% concreto confinado activo modelo de Park% MAT(I,1) = IDCA;              identificador de la categoría% MAT(I,1) = 122;               identificador del modelo material% MAT(I,2) = FCCO;              resistencia a la compresión% MAT(I,3) = ECCO;              deformación máxima% MAT(I,4) = ECSR;              deformación de agrietamiento% MAT(I,5) = ECSP;              deformación de desprendimiento% MAT(I,6) = FCTR;              resistencia a la tracción% MAT(I,7) = 0;                 parámetro no utilizado% MAT(I,8) = 0;                 parámetro no utilizado% MAT(I,9) = RECB;              recubrimiento% MAT(I,10) = RTRE;             espaciamiento entre ref transv% MAT(I,11) = RTRT;             tipo de refuerzo transversal% MAT(I,12) = RTRD;             díametro del ref transversal% ------------------------------------------% concreto confinado pasivo modelo de Madas% MAT(I,1) = IDCA;              identificador de la categoría% MAT(I,1) = 141;               identificador del modelo material% MAT(I,2) = FCCO;            