% construcción de tabla completa de categoríasfunction [CAE] = CATEGO(CAT,MAT)  % Entrada:  % CAT():  tabla del identificador IDCA de la categoría en el archivo .msh de GMSH.  % MAT():  tabla de parámetros de la categoría en el archivo .m   %         % Salida:  % CAE():  tabla completa de categorías. El número de la fila indica el   %         número de la categoría dado en la primera columna de la tabla  %         de conectividades ELE.  NMAT = size(MAT,1);  NCAT = size(CAT,1);  SICA=0;  for ICAT=1:NCAT    for IMAT=1:NMAT      if CAT(ICAT)==MAT(IMAT,1)        CAE(ICAT,:)=MAT(IMAT,:);        SICA=1;      end % endif    end % endfor ICAM    if SICA==0      error('MKAPPA. La tabla CAM no contiene una categoría IDCA definida en GMSH');    end % endif  end % endfor ICAT  end